package com.thirdware.corejava.basics;

public class DefaultValueOfDatatypes {
	static int a;
	static byte b;
	static short c;
	static char d;
	static boolean e;
	static long f;
	static float g;
	static double h;

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Default value of Boolean is : " + e);
		System.out.println("Default value of Char is    : " + d);
		System.out.println("Default value of Byte is    : " + b);
		System.out.println("Default value of Short is   : " + c);
        System.out.println("Default value of Integer is : " + a);
		System.out.println("Default value of Long is    : " + f);
		System.out.println("Default value of Float is   : " + g);
		System.out.println("Default value of Double is  : " + h);
	}

}
